sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    if (isLoading || newValue == '' || isTemplate) {
        return;
    }

    // var whatneeded = g_form.getValue('what_needed');

    // Clear all of the choices from the What needed field choice list
    g_form.clearOptions('what_needed');

    // choice list
    if (newValue == 'leave') {
		g_form.addOption('what_needed', 'holiday', 'Holiday');
		g_form.addOption('what_needed', 'sick Leave', 'Sick Leave');
		g_form.addOption('what_needed', 'annual Leave', 'Annual Leave');
		g_form.addOption('what_needed', 'child Care', 'Child Care');
		g_form.addOption('what_needed', 'marriage', 'Marriage');
		g_form.addOption('what_needed', 'optional Holiday', 'Optional Holiday');
    }

    // choice list
    if (newValue == 'travel') {
		g_form.addOption('what_needed', 'air', 'Air');
		g_form.addOption('what_needed', 'train', 'Train');
		g_form.addOption('what_needed', 'hotel', 'Hotel');
		g_form.addOption('what_needed', 'meal', 'Meal');
    }

    // choice list
    if (newValue == 'work') {
		g_form.addOption('what_needed', 'phone', 'Phone');
		g_form.addOption('what_needed', 'pc', 'PC');
		g_form.addOption('what_needed', 'seat', 'Seat');
		g_form.addOption('what_needed', 'meeting room', 'Meeting room');
    }

    // If the form is loading and it is not a new record, set the u_what_needed value to the
    // value from the record before it was loaded
    // if (isLoading && !g_form.isNewRecord()) {
        // g_form.setValue('what_needed', whatneeded);
    // }
}
