sys_script_client.script:
function onLoad() {
    //Check to see if the form is for a new record.  If it is a new record, 
    //set the Requested for value to the currently logged in user.
    //set the Requested for Email value to the currently logged in user's Email.
    //set the Request Date value to the day of today.

    if(g_form.isNewRecord()){
		// ------设定Requested for
        g_form.setValue('requested_for',g_user.userID);

		// ------设定Requested for email
        // Instantiate the GetEmailAddress Script Include 
        var getEmailAddr = new GlideAjax('GetEmailAddress');
        // Specify the getEmail method
        getEmailAddr.addParam('sysparm_name','getEmail');
        // Pass the Requested for sys_id
        getEmailAddr.addParam('sysparm_userID', g_form.getValue('requested_for'));
        // Send the request to the server
        getEmailAddr.getXML(populateEmailField);

        // // Clear all of the choices from the What needed field choice list
        // g_form.clearOptions('what_needed');

		// // ------设定What needed
		// g_form.addOption('what_needed', 'holiday', 'Holiday');
		// g_form.addOption('what_needed', 'sick Leave', 'Sick Leave');
		// g_form.addOption('what_needed', 'annual Leave', 'Annual Leave');
		// g_form.addOption('what_needed', 'child Care', 'Child Care');
		// g_form.addOption('what_needed', 'marriage', 'Marriage');
		// g_form.addOption('what_needed', 'optional Holiday', 'Optional Holiday');

    }

    function populateEmailField(response){
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the Requested for email field
        var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.setValue('requested_for_email',emailFromScriptInclude);
    }
}
